import React, {Component} from 'react';
import './App.css';
import axios from "axios";

import Component1 from './Components/Component1/C1';
import Component2 from './Components/Component2/C2';

const TEST_URL = "https://catfact.ninja/fact"

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            placeholder: "www.placeholder.com",
            current_fact: "",
            editable_input:{
                text: ""
            }
        }
    }


    handleInputUpdate = (e) => {
        const {value} = e.target;
        console.log(value)
        this.setState(prevState => ({
            editable_input: {
                ...prevState.editable_input,
                text: value
            }
        }))
    }

    handleSendButton = (e) => {
        console.log('I am clicking button')
        var val = this.state.editable_input.text
        console.log(val)
        const {value, name} = e.target;
        console.log(name)

        // this.setState(prevstate =>({
        //     placeholder:{
        //         ...prevstate.placeholder,
        //         placeholder:this.state.current_address
        //     }
        // }))
        console.log(this.state.placeholder)
    }


    render(){
        return(
            <div className = "App">
                <header className="App-header">
                    <p id="welcome">
                        AFTHA PROJECT
                    </p>
                    <Component1 placeholderValue={this.state.placeholder}/>
                    <Component2
                        handleChange ={this.handleInputUpdate}
                        handleClick ={this.handleSendButton}
                    />
                </header>
            </div>
        )
    }
}

export default App
