import React, {Component} from 'react';
import './App.css';
import axios from "axios";

import Component1 from './Components/Component1/C1';
import Component2 from './Components/Component2/C2';

const TEST_URL = "https://catfact.ninja/fact"

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            current_fact: "",
            current_address: ""
        }
    }


    handleInputUpdate = (e) => {
        const [value, name] = e.target;
        this.setState(prevstate => ({
            current_address:{
                ...prevstate.current_address,
                [name] : [value]
            }
        }))

    }

    render(){
        return(
            <div className = "App">
                <header className="App-header">
                    <p id="welcome">
                        AFTHA PROJECT
                    </p>
                    <Component1 />
                    <Component2 handleChange ={this.handleInputUpdate}/>
                </header>
            </div>
        )
    }
}

export default App
