import React, {Component} from 'react';
import './App.css';
import axios from "axios";

import Component1 from './Components/Component1/C1';
import Component2 from './Components/Component2/C2';

const TEST_URL = "https://catfact.ninja/fact"

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            editable_input:{
                placeholder: "www.placeholder.com"
            },
            current_fact: "",
            current_address: ""
        }
    }


    handleInputUpdate = (e) => {
        const {value, name} = e.target;
        console.log(value)
        this.setState(prevstate => ({
            current_address:{
                ...prevstate.current_address,
                current_address: value
            }
        }))
        console.log(value)
    }

    handleSendButton = () =>{
        console.log('I am clicking button')
        const value = this.state.current_address
        console.log(value)
        // this.setState(prevstate => ({
        //     placeholder:{
        //         ...prevstate.placeholder,
        //         placeholder:this.state.current_address
        //     }
        // }))
        console.log(this.state.placeholder)
    }


    render(){
        return(
            <div className = "App">
                <header className="App-header">
                    <p id="welcome">
                        AFTHA PROJECT
                    </p>
                    <Component1 placeholderValue={this.state.editable_input.placeholder}/>
                    <Component2 handleChange ={this.handleInputUpdate} handleClick ={this.handleSendButton}/>
                </header>
            </div>
        )
    }
}

export default App
